#mt_shoppingcart_item
DROP TABLE IF EXISTS `mt_shoppingcart_item`;
CREATE TABLE `mt_shoppingcart_item`(
    `iid` int(11) NOT NULL auto_increment, -- 购物车项的编号
    `user_id` int(11) default NULL,  -- 原来用的是user_id   uid是用户的id,在任何情况下只要表示一个意思,永远用这一个词
    `product_id` int(11) default NULL,  -- 商品编号lid
    `count` int(11) default NULL,
    `is_checked` tinyint(1) default NULL,
    PRIMARY KEY (`iid`)
);

#mt_user
DROP TABLE IF EXISTS `mt_user`;
CREATE TABLE `mt_user` (
    `uid` int(11) NOT NULL auto_increment,
    `uname` varchar(32) default NULL,
    `upwd` varchar(64) default NULL,
    `email` varchar(64) default NULL,
    `phone` varchar(16) default NULL,
    `avatar` varchar(128) default NULL,
    `user_name` varchar(32) default NULL,
    `gender` int(11) default NULL,
    PRIMARY KEY (`uid`)
);



